import { useState } from "react"
// import "SortingComponent.css";

export default function SortingComponent() {
    let [arrayToSort,setArrayToSort] = useState([]);

    const updateSortingArea = () => {
        const inputArray = document.getElementById("arrayInput").value;
        const array = inputArray.split(",");
        setArrayToSort(array);
    }

    return (
        <div>
        <input id="arrayInput" type="text" onChange={updateSortingArea} />
        <div> 
             <NumberBox array={arrayToSort}/>           
        </div>
       </div>
    )       
}
const NumberBox = (props) => {
    return (
        <div>{props.array[1]}</div>
    )
}
