import { useEffect, useState } from "react"
import "./sortingComponent.css";

export default function SortingComponent() {
    let [arrayToSort,setArrayToSort] = useState([4,3,2,1,0,-1,-2,-3,-4]);
    let [compareBoxes,setComapareBoxes] = useState([]);
    let [completedIteration,setcompletedIteration] = useState(0);
    let [elementIndexOfSortedArray,setElementIndexOfSortedArray] = useState([]);

    const updateSortingArea = () => {
        let array = document.getElementById("arrayInput").value.split(",");
        array = array.map(arrayElement => parseInt(arrayElement));
        setArrayToSort(array);
    }   

    useEffect(() => {
        console.log(arrayToSort);
    });

    const generateRandomNumber = () => {
        let randomNumberArray = [];
        for(let i = 0 ; i < 10 ; i++) {
            randomNumberArray.push(parseInt(Math.random() * 100));
        }
        document.getElementById("arrayInput").value = randomNumberArray.toString(); 
        setArrayToSort(randomNumberArray);
    }

    const setColor = (index) => {


        if(compareBoxes.includes(index)) return {'background-color' : 'yellow'};
        else if(((arrayToSort.length - 1) - completedIteration) < index && index <= arrayToSort.length - 1) return {'background-color' : 'green'}; 
        else return {'background-color' : 'grey'};
    }

    const bubbleSort = () => {

        let timeOut = 500;
        let comparedBoxesPosition = [];
        let bufferArrayforPositionOfSortedArray = [];

        const n = arrayToSort.length;
        const array = arrayToSort;

        for(let i = 0; i < n ; i++) {
            bufferArrayforPositionOfSortedArray.push(n-i);
            for(let j = 0 ; j < n-i-1  ; j++) {
                setTimeout(() => {
                    comparedBoxesPosition = (i === n-2) ? [] : [j,j+1];
                    setComapareBoxes(comparedBoxesPosition);
                    setElementIndexOfSortedArray(bufferArrayforPositionOfSortedArray);
                    setcompletedIteration(i);
                    if(array[j] > array[j+1]) swap(array,j,j+1);          
                    setArrayToSort([...array]);
                },  timeOut += 200);

            } 

        }
    }   

    return (
        <div>
        <input id="arrayInput" type="text" onChange={updateSortingArea} /> 

        <div className="numberBoxArea"> 
            {arrayToSort.map((arrayElement,index) => <div className="box" key={index} style={setColor(index)} id={index}> {arrayElement} </div>)}
        </div>
        <button onClick={bubbleSort}>Start</button>
        <button onClick={generateRandomNumber}> Random Numbers </button>
       </div>
    )       
}


const swap = (array,swapElementA,swapElementB) => {
    const temp = array[swapElementA];
    array[swapElementA] = array[swapElementB];
    array[swapElementB] = temp;         
}
