import { useEffect, useState } from "react"
import "./commonStyle.css";
import swap from "./swap";
import { liveAlgorithmData,arrayToSort } from "./common/commonData";
import { colorIndexesSettings } from "./common/commonData";
import NavigationComponent from "./navigationComponent";
import AlgorithmBox from "./common/algorithmSortingBox";

export default function SortingComponent() {
    let [array,setArray] = useState(arrayToSort);
    let [ithIndex,setIthIndex] = useState(0);
    let [jthIndex,setJthIndex] = useState(0);


    useEffect(() => {
        const finalIndexes = [];
        for(let i = arrayToSort.length - 1 ; i > arrayToSort.length - ithIndex - 1 ; i-- ) finalIndexes.push(i);
        const comparedBoxesPosition = (ithIndex === arrayToSort.length-2) ? [] : [jthIndex,jthIndex+1];

        colorIndexesSettings.compareColorIndex = comparedBoxesPosition;
        colorIndexesSettings.swapColorIndex = comparedBoxesPosition;
        colorIndexesSettings.sortedColorIndex = finalIndexes;

    },[ithIndex,jthIndex,array]);


    // const generateRandomNumber = () => {
    //     liveAlgorithmData.sortedStatus = false;
    //     let randomNumberArray = [];
    //     for(let i = 0 ; i < 10 ; i++) {
    //         randomNumberArray.push(parseInt(Math.random() * 100));
    //     }
    //     document.getElementById("arrayInput").value = randomNumberArray.toString(); 
    //     setArray(randomNumberArray);
    // }

    const bubbleSort = () => {
        let timeOut = 1000;
        const n = array.length;
        const currentArray = array;
        for(let i = 0; i < n ; i++) {
            for(let j = 0 ; j < n-i-1  ; j++) {
                setTimeout(() => {
                    setIthIndex(i);
                    setJthIndex(j);
                    if(currentArray[j] > currentArray[j+1]) swap(currentArray,j,j+1);
                    setArray([...currentArray]);
                    if(i === n - 2 && j === n - i - 2) liveAlgorithmData.sortedStatus = true;
                },  timeOut += parseInt(liveAlgorithmData.speedTimeCount));
            }    
        }
    }   

    return (
        <div>

            <NavigationComponent />
            <div className="inputControllerComponent" >
            {/* <button onClick={generateRandomNumber}>Random Numbers</button>    
            <input id="arrayInput" type="text" onChange={updateSortingArea}/>
            <button onClick={bubbleSort}>Start</button>
            <TimeSpeed /> */}
             </div>

            <div className="areaOfAlgorithmsVisualization">
                <AlgorithmBox array={array} />
            </div>
       </div>
    )       
}
