import { useEffect, useState } from "react"
import "./sortingComponent.css";

export default function SortingComponent() {
    let [arrayToSort,setArrayToSort] = useState([4,3,2,1,0]);

    const updateSortingArea = () => {
        let array = document.getElementById("arrayInput").value.split(",");
        array = array.map(arrayElement => parseInt(arrayElement));
        setArrayToSort(array);
    }   

    useEffect(() => {
        console.log(arrayToSort);
    });

    const bubbleSort = () => {
        const n = arrayToSort.length;
        const array = arrayToSort;
        for(let i = 0; i <= n - 1 ; i++) {
            for(let j = 0 ; j <= n-i-1 ; j++) {
                setTimeout(() => {
                    if(array[j] > array[j+1]) {
                    const temp = array[j];
                    array[j] = array[j+1];
                    array[j+1] = temp;                    
                    }
                    setArrayToSort([...arrayToSort]);
                }, 1000);
            }

        }
    }   

    // function Sort() {
    //     let timeout = 1000;
    //     let l = arrayToSort.length;

    //       let temp = [...arrayToSort];
    //       for (let i = 0; i < l - 1; i++) {
    //         for (let j = 0; j < (l - i - 1); j++) {
    //           setTimeout(() => {
    //             if (temp[j] > temp[j + 1]) {
    //               let t = temp[j];
    //               temp[j] = temp[j + 1];
    //               temp[j + 1] = t;
    //             }
    //             setArrayToSort([...temp])
    //           }, timeout += 3000)
    //         }
    //     }

    //   }

    return (
        <div>
        <input id="arrayInput" type="text" onChange={updateSortingArea} />
        <div className="numberBoxArea"> 
            {arrayToSort.map((arrayElement,index) => <div className="box" key={index}> {arrayElement} </div>)}
        </div>
        <button onClick={bubbleSort}>Start</button>
       </div>
    )       
}
