import { useState } from "react"
import "./sortingComponent.css";

export default function SortingComponent() {
    let [arrayToSort,setArrayToSort] = useState([]);

    const updateSortingArea = () => {
        const array = document.getElementById("arrayInput").value.split(",");
        setArrayToSort(array);
    }

    const sortingInitiation = () => {
        bubbleSort();
    }

    const bubbleSort = () => {
        const n = arrayToSort.length;
        console.log(arrayToSort);
        for(let i = 0; i <= n - 1 ; i++) {
            for(let j = 0 ; j <= n-i-1 ; j++) {
                if(arrayToSort[j] > arrayToSort[j+1]) swap(j,j+1);
                setArrayToSort(arrayToSort);
            }
        }

    }
    const swap = (a,b) => {
        const temp = arrayToSort[a];
        arrayToSort[a] = arrayToSort[b];
        arrayToSort[b] = temp;
    }

    return (
        <div>
        <input id="arrayInput" type="text" onChange={updateSortingArea} />
        <div class="numberBoxArea"> 
             <NumberBox array={arrayToSort}/>           
        </div>
        <button onClick={sortingInitiation}>Start</button>
       </div>
    )       
}

const NumberBox = (props) => {
    const array = props.array;
    const listItem = array.map((arrayElement,index) => <div class="box" id={index} >{arrayElement}</div>)
    return (
        <div>{listItem}</div>
    )
}
